import React from 'react';
import { useParams } from 'react-router-dom';

const ServiceDetailPage = () => {
  const { title } = useParams(); // Obter o título do serviço da URL

  // Aqui você pode buscar os detalhes do serviço com base no título
  const services = [
    { title: "odontologia-geral", description: "Tratamentos completos para manter a sua saúde bucal em dia." },
    { title: "ortodontia", description: "Correção do alinhamento dos dentes." },
    { title: "implantes-dentarios", description: "Substitua dentes perdidos com implantes." },
    { title: "endodontia", description: "Tratamento de canal especializado." },
    { title: "estetica-dental", description: "Procedimentos para melhorar a aparência do seu sorriso." },
    { title: "odontopediatria", description: "Cuidados dentários especializados para crianças." }
  ];

  const service = services.find(s => s.title === title); // Encontrar o serviço pelo título

  return (
    <div>
      {service ? (
        <>
          <h1>{service.title.replace(/-/g, ' ')}</h1>
          <p>{service.description}</p>
        </>
      ) : (
        <p>Serviço não encontrado.</p>
      )}
    </div>
  );
};

export default ServiceDetailPage;
