import axios from 'axios';
import authHeader from './auth-header';

// Detectar automaticamente o ambiente e configurar a URL base
const baseURL = process.env.REACT_APP_API_URL || 
               (window.location.hostname === 'localhost' ? 
                'http://localhost:8000/api/' : 
                '/api/');

const API_URL = `${baseURL}clientes/`;

class ClienteService {
  async getClienteProfile() {
    const user = AuthService.getCurrentUser();
    if (!user) {
      throw new Error('User not authenticated');
    }
    
    // Try to get profile using user ID
    return axios.get(API_URL + user.user.id, { headers: authHeader() });
  }
  
  async updateClienteProfile(clienteData) {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) {
      throw new Error('User not authenticated');
    }
    
    // Use the user ID from the authenticated user
    return axios.put(API_URL + user.user.id, clienteData, { headers: authHeader() });
  }
  
  async getAgendamentos() {
    return axios.get(API_URL + 'agendamentos', { headers: authHeader() });
  }
  
  async updateHistorico(historicoData) {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) {
      throw new Error('User not authenticated');
    }
    
    return axios.post(API_URL + user.user.id + '/historico', 
      { historico: JSON.stringify(historicoData) }, 
      { headers: authHeader() }
    );
  }
}

export default new ClienteService();
