import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:8000/api/clientes/';

class ClienteService {
  async getClienteProfile() {
    // This should match the route defined in cliente.routes.js
    return axios.get(API_URL + 'perfil', { headers: authHeader() });
  }
  
  async updateClienteProfile(clienteData) {
    // This should also match the route defined in cliente.routes.js
    return axios.put(API_URL + 'perfil', clienteData, { headers: authHeader() });
  }
  
  // Add method to get client appointments
  async getAgendamentos() {
    return axios.get(API_URL + 'agendamentos', { headers: authHeader() });
  }
}

export default new ClienteService();
