import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import AuthService from '../services/auth.service';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const PageContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2rem;
  background-color: #f8f9fa;
`;

const RegisterCard = styled.div`
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  padding: 2.5rem;
  width: 100%;
  max-width: 600px;
`;

const CardTitle = styled.h2`
  font-size: 2rem;
  margin-bottom: 1.5rem;
  color: #2c3e50;
  text-align: center;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  margin-bottom: 0.5rem;
  color: #2c3e50;
  font-weight: 500;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #e0e0e0;
  border-radius: 4px;
  font-size: 1rem;
  transition: border-color 0.3s;

  &:focus {
    border-color: #3498db;
    outline: none;
  }
`;

const Select = styled.select`
  padding: 0.75rem;
  border: 1px solid #e0e0e0;
  border-radius: 4px;
  font-size: 1rem;
  transition: border-color 0.3s;

  &:focus {
    border-color: #3498db;
    outline: none;
  }
`;

const Button = styled.button`
  padding: 0.75rem 1.5rem;
  background-color: #3498db;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s;
  margin-top: 0.5rem;

  &:disabled {
    background-color: #9cb3c9;
    cursor: not-allowed;
  }

  &:not(:disabled):hover {
    background-color: #2980b9;
  }
`;

const AlertMessage = styled.div`
  padding: 0.75rem;
  border-radius: 4px;
  margin-bottom: 1rem;

  &.success {
    background-color: #d4edda;
    color: #155724;
  }

  &.error {
    background-color: #f8d7da;
    color: #721c24;
  }
`;

const LoginLink = styled.p`
  text-align: center;
  margin-top: 1.5rem;
  color: #7f8c8d;

  a {
    color: #3498db;
    text-decoration: none;
    font-weight: 500;
    transition: color 0.3s;

    &:hover {
      color: #2980b9;
      text-decoration: underline;
    }
  }
`;

const RoleSelector = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 1.5rem;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const RoleOption = styled.div`
  flex: 1;
  padding: 1rem;
  border: 2px solid ${props => props.selected ? '#3498db' : '#e0e0e0'};
  border-radius: 4px;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s;
  background-color: ${props => props.selected ? '#f0f7fc' : '#fff'};

  &:hover {
    border-color: #3498db;
  }

  .role-icon {
    font-size: 1.5rem;
    margin-bottom: 0.5rem;
    color: ${props => props.selected ? '#3498db' : '#7f8c8d'};
  }

  .role-title {
    font-weight: 600;
    color: #2c3e50;
  }

  .role-desc {
    font-size: 0.8rem;
    color: #7f8c8d;
    margin-top: 0.5rem;
  }
`;

const FormRow = styled.div`
  display: flex;
  gap: 1rem;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1.5rem;
  }
`;

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('cliente');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [successful, setSuccessful] = useState(false);
  const [especialidades, setEspecialidades] = useState([]);

  // Campos adicionais para cliente
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [dataNascimento, setDataNascimento] = useState('');
  const [morada, setMorada] = useState('');
  const [nif, setNif] = useState('');

  // Campos adicionais para médico
  const [especialidade_id, setEspecialidadeId] = useState('');
  const [cedulaProfissional, setCedulaProfissional] = useState('');

  // Campos adicionais para admin
  const [departamento, setDepartamento] = useState('');
  const [cargo, setCargo] = useState('');

  const navigate = useNavigate();

  // Buscar especialidades para médicos
  useEffect(() => {
    const fetchEspecialidades = async () => {
      try {
        // Aqui você deve implementar um serviço para buscar especialidades
        // Por enquanto, vamos usar dados de exemplo
        setEspecialidades([
          { id: 1, nome: 'Odontologia Geral' },
          { id: 2, nome: 'Ortodontia' },
          { id: 3, nome: 'Endodontia' },
          { id: 4, nome: 'Periodontia' },
          { id: 5, nome: 'Implantodontia' }
        ]);
      } catch (error) {
        console.error('Erro ao buscar especialidades:', error);
      }
    };

    fetchEspecialidades();
  }, []);

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage('');
    setLoading(true);
    setSuccessful(false);

    try {
      let userData = {};

      // Dados específicos para cada tipo de usuário
      if (role === 'cliente') {
        userData = {
          nome,
          telefone,
          dataNascimento,
          morada,
          nif
        };
      } else if (role === 'medico') {
        userData = {
          nome,
          telefone,
          especialidade_id,
          cedulaProfissional
        };
      } else if (role === 'admin') {
        userData = {
          nome,
          telefone,
          departamento,
          cargo
        };
      }

      await AuthService.register(username, email, password, role, userData);
      setSuccessful(true);
      setMessage('Registo efetuado com sucesso! Pode fazer login agora.');
      setLoading(false);

      // Redirecionar para a página de login após um breve atraso
      setTimeout(() => {
        navigate('/login');
      }, 3000);
    } catch (error) {
      const resMessage =
        (error.response &&
          error.response.data &&
          error.response.data.message) ||
        error.message ||
        error.toString();

      setMessage(resMessage);
      setSuccessful(false);
      setLoading(false);
    }
  };

  return (
    <PageContainer>
      <Navbar />

      <MainContent>
        <RegisterCard>
          <CardTitle>Registar</CardTitle>

          {message && (
            <AlertMessage className={successful ? 'success' : 'error'}>
              {message}
            </AlertMessage>
          )}

          <Form onSubmit={handleRegister}>
            <RoleSelector>
              <RoleOption 
                selected={role === 'cliente'} 
                onClick={() => setRole('cliente')}
              >
                <div className="role-icon">👤</div>
                <div className="role-title">Cliente</div>
                <div className="role-desc">Registe-se como paciente para marcar consultas</div>
              </RoleOption>

              <RoleOption 
                selected={role === 'medico'} 
                onClick={() => setRole('medico')}
              >
                <div className="role-icon">👨‍⚕️</div>
                <div className="role-title">Médico</div>
                <div className="role-desc">Registe-se como médico para gerir consultas</div>
              </RoleOption>

              <RoleOption 
                selected={role === 'admin'} 
                onClick={() => setRole('admin')}
              >
                <div className="role-icon">🔑</div>
                <div className="role-title">Admin</div>
                <div className="role-desc">Acesso administrativo ao sistema</div>
              </RoleOption>
            </RoleSelector>

            <FormGroup>
              <Label htmlFor="username">Nome de Utilizador</Label>
              <Input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="email">Email</Label>
              <Input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="password">Senha</Label>
              <Input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="nome">Nome Completo</Label>
              <Input
                type="text"
                id="nome"
                value={nome}
                onChange={(e) => setNome(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="telefone">Telefone</Label>
              <Input
                type="tel"
                id="telefone"
                value={telefone}
                onChange={(e) => setTelefone(e.target.value)}
                required
              />
            </FormGroup>

            {/* Campos específicos para cliente */}
            {role === 'cliente' && (
              <>
                <FormRow>
                  <FormGroup>
                    <Label htmlFor="dataNascimento">Data de Nascimento</Label>
                    <Input
                      type="date"
                      id="dataNascimento"
                      value={dataNascimento}
                      onChange={(e) => setDataNascimento(e.target.value)}
                      required
                    />
                  </FormGroup>

                  <FormGroup>
                    <Label htmlFor="nif">NIF</Label>
                    <Input
                      type="text"
                      id="nif"
                      value={nif}
                      onChange={(e) => setNif(e.target.value)}
                      required
                    />
                  </FormGroup>
                </FormRow>

                <FormGroup>
                  <Label htmlFor="morada">Morada</Label>
                  <Input
                    type="text"
                    id="morada"
                    value={morada}
                    onChange={(e) => setMorada(e.target.value)}
                    required
                  />
                </FormGroup>
              </>
            )}

            {/* Campos específicos para médico */}
            {role === 'medico' && (
              <>
                <FormGroup>
                  <Label htmlFor="especialidade">Especialidade</Label>
                  <Select
                    id="especialidade"
                    value={especialidade_id}
                    onChange={(e) => setEspecialidadeId(e.target.value)}
                    required
                  >
                    <option value="">Selecione uma especialidade</option>
                    {especialidades.map(esp => (
                      <option key={esp.id} value={esp.id}>
                        {esp.nome}
                      </option>
                    ))}
                  </Select>
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="cedulaProfissional">Cédula Profissional</Label>
                  <Input
                    type="text"
                    id="cedulaProfissional"
                    value={cedulaProfissional}
                    onChange={(e) => setCedulaProfissional(e.target.value)}
                    required
                  />
                </FormGroup>
              </>
            )}

            {/* Campos específicos para admin */}
            {role === 'admin' && (
              <>
                <FormRow>
                  <FormGroup>
                    <Label htmlFor="departamento">Departamento</Label>
                    <Input
                      type="text"
                      id="departamento"
                      value={departamento}
                      onChange={(e) => setDepartamento(e.target.value)}
                      required
                    />
                  </FormGroup>

                  <FormGroup>
                    <Label htmlFor="cargo">Cargo</Label>
                    <Input
                      type="text"
                      id="cargo"
                      value={cargo}
                      onChange={(e) => setCargo(e.target.value)}
                      required
                    />
                  </FormGroup>
                </FormRow>
              </>
            )}

            <Button type="submit" disabled={loading}>
              {loading ? 'A processar...' : 'Registar'}
            </Button>

            <LoginLink>
              Já tem uma conta? <Link to="/login">Faça login</Link>
            </LoginLink>
          </Form>
        </RegisterCard>
      </MainContent>

      <Footer />
    </PageContainer>
  );
}

export default RegisterPage;
