import axios from 'axios';
import AuthService from './auth.service';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

class ClienteService {
  constructor() {
    this.user = AuthService.getCurrentUser();
    this.authHeader = this.user?.accessToken 
      ? { Authorization: `Bearer ${this.user.accessToken}` } 
      : {};
  }

  // Obter perfil do cliente atual (usu치rio logado)
  async getClienteProfile() {
    console.log("Obtendo perfil do cliente com token:", this.authHeader);
    try {
      const response = await axios.get(`${API_URL}/cliente/perfil`, {
        headers: this.authHeader
      });
      console.log("Resposta do perfil do cliente:", response.data);
      return response;
    } catch (error) {
      console.error("Erro ao obter perfil:", error);
      throw error;
    }
  }

  // Atualizar perfil do cliente
  async updateClienteProfile(clienteData) {
    return axios.put(`${API_URL}/cliente/perfil`, clienteData, {
      headers: this.authHeader
    });
  }

  // Obter hist칩rico de consultas do cliente
  async getConsultas() {
    return axios.get(`${API_URL}/cliente/consultas`, {
      headers: this.authHeader
    });
  }
}

export default new ClienteService();
