import axios from 'axios';
import authHeader from './auth-header';
import AuthService from './auth.service';

// Detectar automaticamente o ambiente e configurar a URL base
const baseURL = process.env.REACT_APP_API_URL || 
               (window.location.hostname === 'localhost' ? 
                'http://localhost:8000/api/' : 
                '/api/');

const API_URL = `${baseURL}clientes/`;

class ClienteService {
  async getClienteProfile() {
    const user = AuthService.getCurrentUser();
    if (!user) {
      throw new Error('User not authenticated');
    }
    
    // Check the structure of the user object and access ID correctly
    const userId = user.id || (user.user && user.user.id);
    if (!userId) {
      console.error('User object structure:', user);
      throw new Error('User ID not found in authenticated user object');
    }
    
    // Try to get profile using user ID
    return axios.get(API_URL + userId, { headers: authHeader() });
  }
  
  async updateClienteProfile(clienteData) {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) {
      throw new Error('User not authenticated');
    }
    
    // Check the structure of the user object and access ID correctly
    const userId = user.id || (user.user && user.user.id);
    if (!userId) {
      console.error('User object structure:', user);
      throw new Error('User ID not found in authenticated user object');
    }
    
    // Log the URL we're trying to access for debugging
    console.log('Attempting to update profile at:', `${baseURL}usuarios/${userId}`);
    
    // Try a different API endpoint structure - use usuarios instead of clientes
    return axios.put(`${baseURL}usuarios/${userId}`, clienteData, { headers: authHeader() });
  }
  
  async getAgendamentos() {
    return axios.get(API_URL + 'agendamentos', { headers: authHeader() });
  }
  
  async updateHistorico(historicoData) {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) {
      throw new Error('User not authenticated');
    }
    
    return axios.post(API_URL + user.user.id + '/historico', 
      { historico: JSON.stringify(historicoData) }, 
      { headers: authHeader() }
    );
  }
}

export default new ClienteService();
